void sharpen_filter::thread_hdltv_gen() {
    const char* dump_tv = std::getenv("AP_WRITE_TV");
    if (!(dump_tv && string(dump_tv) == "on")) return;

    wait();

    mHdltvinHandle << "[ " << endl;
    mHdltvoutHandle << "[ " << endl;
    int ap_cycleNo = 0;
    while (1) {
        wait();
        const char* mComma = ap_cycleNo == 0 ? " " : ", " ;
        mHdltvinHandle << mComma << "{"  <<  " \"ap_rst_n\" :  \"" << ap_rst_n.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"ap_start\" :  \"" << ap_start.read() << "\" ";
        mHdltvoutHandle << mComma << "{"  <<  " \"ap_done\" :  \"" << ap_done.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"ap_idle\" :  \"" << ap_idle.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"ap_ready\" :  \"" << ap_ready.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"input_r_dout\" :  \"" << input_r_dout.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"input_r_empty_n\" :  \"" << input_r_empty_n.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"input_r_read\" :  \"" << input_r_read.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"output_r_din\" :  \"" << output_r_din.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"output_r_full_n\" :  \"" << output_r_full_n.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"output_r_write\" :  \"" << output_r_write.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"ap_return\" :  \"" << ap_return.read() << "\" ";
        mHdltvinHandle << "}" << std::endl;
        mHdltvoutHandle << "}" << std::endl;
        ap_cycleNo++;
    }
}

